import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useFormik, Formik, Form, Field, Error, validateYupSchema } from 'formik';
import * as Yup from 'yup';
import { Navbar } from '../components/Navbar';
import { CategoryList } from '../components/CategoryList';
import EndPoints from '../api/EndPoints';

const LoginPage = () => {

    const initialValues = {
        userName: '',
        password: ''
    }

    const onSubmit = (values) => {
        console.log(values);
        axios.post(EndPoints.USER_LOGIN_URL, values)
            .then((response) => {
                console.log(response)
                setRequestResponse({
                    textMessage: response.data,
                    alertClass: 'alert alert-success'
                })
            }, (error) => {
                console.log(error);
                setRequestResponse({
                    textMessage: error.response.data,
                    alertClass: 'alert alert-danger'
                })
            })
            .catch(error => {
                console.log(error);
            })
    }

    const validationSchema = Yup.object({
        userName: Yup.string().required('userName is required.'),
        password: Yup.string().required('password is required.').min(6, 'password must contain at least 6 charectors.')
    })

    return (
        <>
            <Navbar />
            <CategoryList />
            <div className="container">
                <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">

                        <div className="registration-wrapper">
                            <h2 style={{ textAlign: 'center', marginBottom: '8%' }}>
                                Login
                            </h2>
                            <Formik
                            initialValues={initialValues}
                            onSubmit={onSubmit}
                            validationSchema={validateYupSchema}>
                                {(formik) => {
                                    return (
                                        <form>
                                            <div className="form-group">
                                                <label >User Name</label>
                                                <Field type="text" name="userName" className="form-control" />
                                            </div>
                                            <div className="form-group">
                                                <label >Password</label>
                                                <Field type="password" name="Password" className="form-control" />
                                            </div>
                                            <input type="submit" value="Login" className="btn btn-block" />

                                        </form>
                                    )
                                }}
                            </Formik>
                            {/* <p className="text-center">
                                Already registered? <a href='/register'>sign up!</a>
                            </p> */}
                        </div>
                    </div>
                    <div className="col-md-3"></div>
                </div>
            </div>
        </>

    )
}

export default LoginPage
