import React,{ useState, useEffect } from 'react';
import axios from 'axios';
import EndPoints from '../api/EndPoints';
// import { Link } from 'react-router-dom';

export const Product = () => {

    const id = 1;
    const [product, setProduct] = useState({});

    const getProduct = () => {
        axios.get(EndPoints.PRODUCTS_LIST_URL + id)
        .then(response => setProduct(response.data))
        .catch(error => console.log(error));
    }

    useEffect(() => {
        getProduct()
    }, [id])

    return (
        <>
            <div className="col-sm-3">
                <div className="card card-style">
                    <img src={product.data.image} alt='' style={{ height: '250px' }} />
                    <div className="card-body">
                        <div>
                            <h6 className="card-title"> <span className='brand-style'>Brand, </span>{product.data.title.slice(0, 10)}...</h6>
                            <p className='card-rating-style'>*****, ({product.datarating.count})</p>
                            <p className='card-price-style'>$ <b>{product.data.price}</b>.00</p>
                        </div>

                        <a href='#'  className="btn btn-primary btn-block">Add to Cart</a>
                    </div>
                </div>
            </div>
        </>
    )
}
