import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useFormik, Formik, Form, Field, Error, validateYupSchema, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { Navbar } from '../components/Navbar';
import { CategoryList } from '../components/CategoryList';
import EndPoints from '../api/EndPoints';

const LoginPage = () => {

    const initialValues = {
        userName: '',
        password: ''
    }

    const onSubmit = (values) => {
        console.log(values);
    }

    const validationSchema = Yup.object({
        userName: Yup.string().required('userName is required.'),
        password: Yup.string().required('password is required.').min(6, 'password must contain at least 6 charectors.')
    })

    return (
        <>
            <Navbar />
            <CategoryList />
            <div className="container">
                <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">

                        <div className="registration-wrapper">
                            <h2 style={{ textAlign: 'center', marginBottom: '8%' }}>
                                Login
                            </h2>
                            <form>
                                <div className="form-group">
                                    <label>userName</label>
                                    <input type="text" name="" id="" className="form-control" />
                                </div>
                                <div className="form-group">
                                    <label>Password</label>
                                    <input type="text" name="" id="" className="form-control" />
                                </div>
                            </form>
                            <p className="text-center">
                                Already registered? <a href='/register'>sign up!</a>
                            </p>
                        </div>
                    </div>
                    <div className="col-md-3"></div>
                </div>
            </div>
        </>

    )
}

export default LoginPage
