import { ActionTypes } from "../constants/action-types";

// initialState
const initialState = {
    numberCart: 0,
    carts: []
}

export const cartReducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case ActionTypes.ADD_TO_CART:
            // cart is empty no need to check directly add item in cart with quantity 1
            // cart is not empty now check item in the cart if item is same increase quantity by 1
            // cart is not empty now check item in the cart if item is not same add item with quantity 1    
            if (state.numberCart === 0) {
                let item = {
                    ...payload,
                    quantity: 1
                };
                state.carts.push(item);
            } else {
                let check = false;
                state.carts.map((item, index) => {
                    if (item._id === payload._id) {
                        state.carts[index].quantity++;
                        check = true
                    }
                });
                if (!check) {
                    let _item = {
                        ...payload,
                        quantity: 1
                    }
                    state.carts.push(_item);
                }
            }
            return {
                ...state,
                numberCart: state.numberCart + 1
            }
        default:
            return state;
    }
}
