import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useFormik, Formik, Form, Field, Error, validateYupSchema, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { Navbar } from '../components/Navbar';
import { CategoryList } from '../components/CategoryList';
import EndPoints from '../api/EndPoints';

const LoginPage = () => {

    const initialValues = {
        userName: '',
        password: ''
    }

    const onSubmit = (values) => {
        console.log(values);
    }

    const validationSchema = Yup.object({
        userName: Yup.string().required('userName is required.'),
        password: Yup.string().required('password is required.').min(6, 'password must contain at least 6 charectors.')
    })

    return (
        <>
            <Navbar />
            <CategoryList />
            <div className="container">
                <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">

                        <div className="registration-wrapper">
                            <h2 style={{ textAlign: 'center', marginBottom: '8%' }}>
                                Login
                            </h2>
                            <Formik
                             initialValues={initialValues}
                             onSubmit={onSubmit}
                             validationSchema={validationSchema}
                            >
                                {(formik) => {
                                    return (
                                        <form>
                                            <div className="form-group">
                                                <label>userName</label>
                                                <Field type="text" name="userName" className={formik.touched.userName && formik.errors.password ? 'form-control is-invalid': 'form-control'}  />
                                                <ErrorMessage name='userName'>
                                                    {(errorMessage) => (
                                                        <small className='text-danger'>
                                                            {errorMessage}
                                                        </small>
                                                    )}
                                                </ErrorMessage>
                                            </div>
                                            <div className="form-group">
                                                <label>Password</label>
                                                <Field type="password" name="password" className={formik.touched.password && formik.errors.password ? 'form-control is-invalid': 'form-control'} />
                                                <ErrorMessage name='password'>
                                                    {(errorMessage) => (
                                                        <small className='text-danger'>
                                                            {errorMessage}
                                                        </small>
                                                    )}
                                                </ErrorMessage>
                                            </div>
                                            <input type="submit" value="Login" className="btn btn-primary btn-log" />
                                        </form>
                                    )
                                }}
                            </Formik>

                            <p className="text-center">
                                Already registered? <a href='/register'>sign up!</a>
                            </p>
                        </div>
                    </div>
                    <div className="col-md-3"></div>
                </div>
            </div>
        </>

    )
}

export default LoginPage
