import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { Navbar } from '../components/Navbar';
import { CategoryList } from '../components/CategoryList';

const LoginPage = () => {

    const [requestedResponse, setRequestResponse] = useState({
        textMessage: '',
        alertClass: ''
    })

    const initialValues = {
        userName: '',
        password: ''
    }

    const onSubmit = (values) => {
        console.log(values);
        axios.post(EndPoints.USER_LOGIN_URL, values)
        .then((response) => {
            console.log(response)
            setRequestResponse({
                textMessage: response.data,
                alertClass: 'alert alert-success'
            })
        }, (error) => {
            console.log(error);
            setRequestResponse({
                textMessage: error.response.data,
                alertClass: 'alert alert-danger'
            })
        })
        .catch(error => {
            console.log(error);
        })
    }

    const validationSchema = Yup.object({
        userName: Yup.string().required('userName is required.'),
        password: Yup.string().required('password is required.').min(6, 'password must contain at least 6 charectors.')
    })


    const formik = useFormik({
        initialValues: initialValues,
        onSubmit: onSubmit,
        validationSchema: validationSchema,
        validateOnMount: true
    })

    return (
        <>
            <Navbar />
            <CategoryList />
            <div className="container">
                <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">
                    <div className={requestedResponse.alertClass} role='alert'>
                                {requestedResponse.textMessage}
                            </div>
                        <div className="registration-wrapper">
                            <h2 style={{ textAlign: 'center', marginBottom: '8%' }}>
                                Login
                            </h2>
                            <form onSubmit={formik.handleSubmit}>
                            <div className="form-group">
                                    <label htmlFor="userName">userName</label>
                                    <input type="text" name='userName' id='userName' className={formik.touched.userName && formik.errors.userName ? 'form-control is-invalid' : 'form-control'} value={formik.values.userName} onChange={formik.handleChange} onBlur={formik.handleBlur} />
                                    {formik.errors.userName && formik.touched.userName ?(<small className='text-danger'>{formik.errors.userName}</small>):null}
                                </div>
                                <div className="form-group">
                                    <label htmlFor="password">Password</label>
                                    <input type="password" name='password' id='password' className={formik.touched.password && formik.errors.password ? 'form-control is-invalid' : 'form-control'} value={formik.values.password} onChange={formik.handleChange} onBlur={formik.handleBlur} />
                                    {formik.errors.password && formik.touched.password ?(<small className='text-danger'>{formik.errors.password}</small>):null}
                                </div>

                                <input type="submit" value="Login" className="btn btn-primary btn-block" disabled={!formik.isValid} />
                            </form>
                            <p className="text-center">
                                Already registered? <a href='/register'>sign up!</a>
                            </p>
                        </div>
                    </div>
                    <div className="col-md-3"></div>
                </div>
            </div>
        </>

    )
}

export default LoginPage
