import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { Navbar } from '../components/Navbar';
import { CategoryList } from '../components/CategoryList';

const LoginPage = () => {

    const initialValues = {
        email: '',
        password: ''
    }

    const onSubmit = (values) => {
        console.log(values);
    }

    const formik = useFormik({
        initialValues: initialValues,
        onSubmit: onSubmit

    })

    return (
        <>
            <Navbar />
            <CategoryList />
            <div className="container">
                <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">
                        <div className="registration-wrapper">
                            <h2 style={{ textAlign: 'center', marginBottom: '8%' }}>
                                Login
                            </h2>
                            <form onSubmit={formik.handleSubmit}>
                            <div className="form-group">
                                    <label htmlFor="email">Email</label>
                                    <input type="text" name='email' id='email' className='form-control' value={formik.values.email} onChange={formik.handleChange} />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="password">Password</label>
                                    <input type="password" name='password' id='password' className='form-control' value={formik.values.password} onChange={formik.handleChange} />
                                </div>

                                <input type="submit" value="Login" className="btn btn-primary btn-block" />
                            </form>
                            <p className="text-center">
                                Already registered? <a href='/login'>sign up!</a>
                            </p>
                        </div>
                    </div>
                    <div className="col-md-3"></div>
                </div>
            </div>
        </>

    )
}

export default LoginPage
